import '../App.css';
import rice from '../img/rice.png';
import { useState } from 'react';
import { data } from '../firebase-config.js';
import { setDoc, doc } from 'firebase/firestore';

function Rice() {
    const [x, setX] = useState(0);
    const [y, setY] = useState(0);

    const handleEdit = async () => {
        const docRef = doc(data, "Total", "Rice");
        const payload = {value : 1};
        setDoc(docRef, payload);
    }

    function randomWidth() {
        return Math.floor(Math.random() * (60 + 60) - 60);
    }
    
    function randomHeight() {
        return Math.floor(Math.random() * (60 + 10) - 10);
    }

    function test() {
        setX(randomWidth);
        setY(randomHeight);
    }

    return (
    <section style={{ backgroundColor: 'rgb(226, 229, 222)' }}>
        <div className='RiceGame'>
            <h2>Rice</h2>
            <p>Help me harvest my rice</p>    
        </div>
        <div className='PlayArea'>
            {/* <img src={rice} style={{ position: 'relative', left: randomWidth() + '%', top: randomHeight() + '%'}}/> */}
            <img src={rice} onClick={handleEdit} style={{ position: 'relative', left: x + '%', top: y + '%'}}/>
        </div>
        <p>Total Rice Harvested: </p>
    </section>
    );
}

export default Rice;
