import '../App.css';
import rice from '../img/rice.png';
import { useState, useEffect } from 'react';
import { data } from '../firebase-config.js';
import { collection, onSnapshot, addDoc } from 'firebase/firestore';

function Rice() {

    const [total, setTotal] = useState([]);
    const [x, setX] = useState(0);
    const [y, setY] = useState(0);
    
    useEffect(
      () => 
        onSnapshot(collection(data, "Total"),(snapshot) => 
        setTotal(snapshot.docs.map(doc => doc.data()))
      ),
      []
    );

    const addRice = async () => {
      const collectionRef = collection(data, "Total");
      const payload = {Rice : total + 1}
      await addDoc(collectionRef, payload);
    }

    function randomWidth() {
        return Math.floor(Math.random() * (60 + 60) - 60);
    }
    
    function randomHeight() {
        return Math.floor(Math.random() * (60 + 10) - 10);
    }

    function test() {
        console.log(total[0].Rice);
        addRice();
        setX(randomWidth);
        setY(randomHeight);
    }

    return (
    <section style={{ backgroundColor: 'rgb(226, 229, 222)' }}>
        <div className='RiceGame'>
            <h2>Rice</h2>
            <p>Help me harvest my rice</p>    
        </div>
        <div className='PlayArea'>
            {/* <img src={rice} style={{ position: 'relative', left: randomWidth() + '%', top: randomHeight() + '%'}}/> */}
            <img src={rice} onClick={test} style={{ position: 'relative', left: x + '%', top: y + '%'}}/>
        </div>
        <p>Total Rice Harvested:</p>
    </section>
    );
}

export default Rice;
